import React, { useReducer } from "react";
import ReactSpeedometer from "react-d3-speedometer";

const initialAmount = {
  speed: 0,
  started: false,
};
const countReduce = (state, action) => {
  switch (action.type) {
    case "START":
      return { ...state, started: true };
    case "STOP":
      if(state.speed)
      return initialAmount;
    case "ACCELERATE":
      if (state.started && state.speed > 0) {
        return { ...state, speed: state.speed + 5 };
      }
      return state;
    case "BRAKE":
      if (state.speed >= 5) {
        return { ...state, speed: state.speed - 5 };
      }
      return state;
    default:
      return state;
  }
}
function Car() {
  const [state, dispatch] = useReducer(countReduce, initialAmount);
  const startFunc = () => {
    state.started ? "ON" : "OFF";
  };
  const accelerate = () => {
    state.speed;
  };
  const brake = () => {
    state.speed === 0;
  };
  return (
    <div className='car'>
      <ReactSpeedometer />
      <span>{startFunc}</span>
      <span>{state.speed}</span>
    </div>
  );
}
export default Car
