import React, { useReducer } from "react";
import ReactSpeedometer from "react-d3-speedometer"
// and just use it
<ReactSpeedometer />

const countReduce = (state, action) => {
  switch (action.type) {
    case "ACCELERATING":
      return { speed: state.speed === 5 };
    case "BRAKING":
      return { speed: state.speed === 5 };
  }
};
export default function Car() {
  const [state, dispatch] = useReducer(countReduce, { speed: 0 });
  return <div className='car'>Make your instrument cluster here</div>;
}
