  switch (action.type) {
    case "START":
      return { started: (state.started = true) };
    case "STOP":
      return initialAmount;
    case "ACCELERATE":
      if(started)
      return { speed: state.speed + 5 };
    case "BRAKE":
      return { speed: state.speed < 5 };
  }
};
const initialAmount = {
  speed: 0,
  started: false,
};
export default function Car() {
  const [state, dispatch] = useReducer(countReduce, initialAmount);
  const startFunc = () => {
    state.started ? "ON" : "OFF";
  };
  const accelerate = () => {
    state.speed;
  };
  const brake = () => {
    state.speed === 0;
  };
  return (
    <div className='car'>
      <ReactSpeedometer />
      <span>{startFunc}</span>
      <span>{state.speed}</span>
    </div>
  );
}
