import React, { useReducer } from "react";
import ReactSpeedometer from "react-d3-speedometer";

const countReduce = (state, action) => {
  switch (action.type) {
    case "ACCELERATING":
      return { speed: state.speed === 5 };
    case "BRAKING":
      return { speed: state.speed === 5 };
  }
};
const initialAmount = {
  speed: 0,
  started: false,
};
export default function Car() {
  const [state, dispatch] = useReducer(countReduce, initialAmount);

  return (
    <div className='car'>
      <ReactSpeedometer />
      <span>{state.started}</span>
      <span>{state.speed}</span>
    </div>
  );
}
